import React from 'react';
import './RecipeCard.css';

const RecipeCard = ({ recipe, onRecipeClick }) => {

    return (
        <div className='Card'>
            <li>
                <h3>{recipe.name}</h3>
                <p>Prep time: {recipe.prepTime} minutes</p>
                <button onClick={() => onRecipeClick(recipe.id)}>Details</button>
            </li>
        </div>
    );
};

export default RecipeCard;
