import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';
import RecipeList from '../components/RecipeList';
import RecipeModal from '../components/RecipeModal';
import { fetchRecipesByIngredients } from '../services/api';
import { fetchRecipesById } from '../services/api';

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleSearch = async (query) => {
    setRecipes([]);
    setError('');
    setLoading(true);

    if (!query.trim()) {
      setError('Please enter a search term.');
      setLoading(false);
      return;
    }

    try {
      if (/^\d+$/.test(query)) {
        // Fetch by ID
        const response = await fetchRecipesById(query);
        console.log("Fetched recipe by ID:", response);
        setRecipes([response]);
      } else if (/^[a-zA-Z\s,]+$/.test(query)) {
        // Fetch by ingredients
        const response = await fetchRecipesByIngredients(query);
        console.log("Fetched recipes by ingredients:", response);
        setRecipes(response);
      } else {
        throw new Error('Invalid input. Please use only letters, numbers, or commas.');
      }
    } catch (err) {
      console.error('Error during search:', err);
      setError(err.message || 'An error occurred during the search.');
    } finally {
      setLoading(false);
    }
  };

  const handleRecipeClick = async (id) => {
    setLoading(true);
    setError('');
    try {
      const recipe = await fetchRecipesById(id);
      setSelectedRecipe(recipe);
    } catch (err) {
      console.error('Error fetching recipe details:', err);
      setError('Unable to fetch recipe details.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1 className='title'>Welcome to a plant-based life!</h1>
      <h2>Find delicious vegan recipes by searching for ingredients or recipe IDs!</h2>
      <SearchBar onSearch={handleSearch} />
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {recipes.length === 0 && !loading && !error && (
        <p>No recipes to display. Start your search above!</p>
      )}
      <RecipeList recipes={recipes} onRecipeClick={handleRecipeClick} />
      {selectedRecipe && (
        <RecipeModal
          recipe={selectedRecipe}
          onClose={() => setSelectedRecipe(null)}
        />
      )}
    </div>
  );
};

export default Home;
