import axios from 'axios';

const API_BASE = 'http://localhost:5000';

export const fetchRecipes = (query) =>
  axios.get(`${API_BASE}/recipes`, { params: { ingredients: query } });

export const fetchRecipeById = (id) =>
  axios.get(`${API_BASE}/recipes/${id}`);
