import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';
import RecipeList from '../components/RecipeList';
import RecipeModal from '../components/RecipeModal';
import { fetchRecipesByIngredients } from '../services/api';
import { fetchRecipeById } from '../services/api';

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleSearch = async (query) => {
    setLoading(true);
    setError('');
    try {
      // Use the API function to fetch recipes based on ingredients
      const response = await fetchRecipesByIngredients(query);
      setRecipes(response);
    } catch (err) {
      setError('Error fetching recipes');
    } finally {
      setLoading(false);
    }
  };

  const handleRecipeClick = async (id) => {
    setLoading(true);
    setError('');
    try {
      // Use the API function to fetch recipe details by ID
      const recipe = await fetchRecipeById(id);
      setSelectedRecipe(recipe);
    } catch (err) {
      setError('Error fetching recipe details');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      <RecipeList recipes={recipes} onRecipeClick={handleRecipeClick} />
      <RecipeModal
        recipe={selectedRecipe}
        onClose={() => setSelectedRecipe(null)}
      />
    </div>
  );
};

export default Home;
