import React, { useState } from 'react';
import { fetchRecipesByIngredients } from '../services/api';

const RecipeList = () => {
  const [ingredients, setIngredients] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const result = await fetchRecipesByIngredients(ingredients);
      setRecipes(result);
    } catch (error) {
      setError('There was an error fetching the recipes. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={ingredients}
        onChange={(e) => setIngredients(e.target.value)}
        placeholder="Enter ingredients"
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Loading...' : 'Search Recipes'}
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ul>
        {recipes.map((recipe) => (
          <li key={recipe.id}>
            <h3>{recipe.name}</h3>
            <p>{recipe.description}</p>
            <p>Prep time: {recipe.prepTime} minutes</p>
            <button>View More</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RecipeList;
